var runnersApp = angular.module('app', [
    'ngRoute',
    'ui.bootstrap',
    'runnersAppCtrls',
    'google-maps'
  ]);
runnersApp.config([
  '$routeProvider',
  function ($routeProvider) {
    $routeProvider.when('/', {
      templateUrl: 'tpl/partials/home.html',
      controller: 'homeCtrl'
    }).when('/members', {
      templateUrl: 'tpl/partials/members.html',
      controller: 'membersCtrl'
    }).when('/locations', {
      templateUrl: 'tpl/partials/locations.html',
      controller: 'locationsCtrl'
    }).when('/newsletters', {
      templateUrl: 'tpl/partials/newsletters.html',
      controller: 'newslettersCtrl'
    }).otherwise({ redirectTo: '/' });
  }
]);var runnersAppCtrls = angular.module('runnersAppCtrls', []);
function navbarController($scope, $location) {
  $scope.isActive = function (viewLocation) {
    return viewLocation === $location.path();
  };
}
